using System.Collections.ObjectModel;

namespace trail_mapper.ViewModels
{
    public class model    {        public double ProdId { get; set; }        public string Prodname { get; set; }        public double Value { get; set; }    }    public class viewmodel    {        public viewmodel()        {            this.Products = new ObservableCollection<model>();            Products.Add(new model() { ProdId = 1, Prodname = "Rice", Value = 53 });            Products.Add(new model() { ProdId = 2, Prodname = "Wheat", Value = 76 });            Products.Add(new model() { ProdId = 3, Prodname = "Oil", Value = 80 });            Products.Add(new model() { ProdId = 4, Prodname = "Corn", Value = 50 });            Products.Add(new model() { ProdId = 5, Prodname = "Gram", Value = 68 });            Products.Add(new model() { ProdId = 6, Prodname = "Milk", Value = 90 });            Products.Add(new model() { ProdId = 7, Prodname = "Butter", Value = 73 });        }        public ObservableCollection<model> Products { get; set; }    }}
